@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e-10") == dec"-0.000005626")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e-5") == dec"-0.5626")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e-2") == dec"-562.6")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e-1") == dec"-5626")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e-0") == dec"-5.626e+4")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e+0") == dec"-5.626e+4")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e+1") == dec"-5.626e+5")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e+2") == dec"-5.626e+6")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e+3") == dec"-5.626e+7")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e+4") == dec"-5.626e+8")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e+5") == dec"-5.626e+9")
@with_context (Emax = 999, Emin = -999, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(normalize(dec"-56260e+6") == dec"-5.626e+10")
