using Decimals
using Test
using Decimals: @with_context

@testset "Subtract" begin
@with_context (Emax = 384, Emin = -383, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(dec"0" - dec"0" == dec"0")
@with_context (Emax = 384, Emin = -383, precision = 9, rounding = RoundingMode{:NearestTiesAway}()) @test(dec"1" - dec"1" == dec"0")
